import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { url } from "../lib/initial-data";

export const fetchCity = createAsyncThunk("city/fetchCity", async (city) => {
  const response = await fetch(url(city));
  const result = await response.json();
  return result;
});

export const cityWeather = createSlice({
  name: "cityWeather",
  initialState: {},
  reducers: {
    cityLoaded: (_, action) => action.payload,
  },
  extraReducers: {
    [fetchCity.fulfilled]: (_, action) => action.payload,
  },
});

export const { cityLoaded } = cityWeather.actions;

export const cityReducer = cityWeather.reducer;
