import React from "react";
import { StaticRouter } from "react-router-dom";
import { Provider } from "react-redux";
import express from "express";
import { configureStore } from "@reduxjs/toolkit";
import { renderToString } from "react-dom/server";
import { matchRoutes } from "react-router-config";
import { ServerStyleSheet } from "styled-components";

import { cityWeatherReducer, cityForecastReducer } from "./features/city";
import { App } from "./App";
import { urls, urlWeather } from "./lib/initial-data";
import { routes } from "./lib/render-routes";

// fix server
const fetch = require("node-fetch");

require("dotenv").config();

const assets = require(process.env.RAZZLE_ASSETS_MANIFEST);

const cssLinksFromAssets = (assets, entrypoint) => {
  return assets[entrypoint]
    ? assets[entrypoint].css
      ? assets[entrypoint].css
          .map((asset) => `<link rel="stylesheet" href="${asset}">`)
          .join("")
      : ""
    : "";
};

const jsScriptTagsFromAssets = (assets, entrypoint, extra = "") => {
  return assets[entrypoint]
    ? assets[entrypoint].js
      ? assets[entrypoint].js
          .map((asset) => `<script src="${asset}"${extra}></script>`)
          .join("")
      : ""
    : "";
};

const createStore = (result) =>
  configureStore({
    reducer: {
      cityWeather: cityWeatherReducer,
      cityForecast: cityForecastReducer,
    },
    preloadedState: {
      cityWeather: result,
    },
  });

const fn = (request, url) => ({ res: new Promise(request), url });

const server = express();
server
  .disable("x-powered-by")
  .use(express.static(process.env.RAZZLE_PUBLIC_DIR))
  .get("/*", (req, res) => {
    const branch = matchRoutes(routes, req.url);
    const promises = [];

    branch.map(({ route, match }) => {
      urls.map(({ key, value }) => {
        if (key === route.path) {
          promises.push({ request: fetch(value(match.params.id)), url: key });
        }
      });
    });

    console.log(promises);

    Promise.all(promises.map(fn))
      .then((res) => {
        console.log("res", res);
        const promise2 = [];
        res.forEach((each) => promise2.push(each.json()));
        return Promise.all(promise2);
      })
      .then((data) => {
        console.log(data);
      });
    // .then((data) => createStore(data))
    // .then((store) => createPage(req, store))
    // .then((render) => res.send(render));
  });

export const cityPage = (req, res) =>
  fetch(urlWeather(req.params.id))
    .then((res) => {
      return res.json();
    })
    .then((result) => createStore(result))
    .then((store) => createPage(req, store))
    .then((render) => res.send(render));

export const mainPage = (req, res) => {
  const store = createStore();
  const render = createPage(req, store);
  res.send(render);
};

const createPage = (req, store) => {
  const sheet = new ServerStyleSheet();

  const markup = renderToString(
    sheet.collectStyles(
      <Provider store={store}>
        <StaticRouter location={req.path}>
          <App />
        </StaticRouter>
      </Provider>
    )
  );

  const styleTags = sheet.getStyleTags();
  const preloadedState = store.getState();

  return `<!doctype html>
    <html lang="">
    <head>
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta charset="utf-8" />
        <title>Welcome to Razzle</title>
        <meta name="viewport" content="width=device-width, initial-scale=1">
        ${cssLinksFromAssets(assets, "client")}
        ${styleTags}
    </head>
    <body style="margin: 0">
        <div id="root">${markup}</div>
        ${jsScriptTagsFromAssets(assets, "client", " defer crossorigin")}
        <script> window.__PRELOADED_STATE__ = ${JSON.stringify(
          preloadedState
        ).replace(/</g, "\\u003c")}</script>
    </body>
</html>`;
};

export default server;
